import React, { useState, useEffect } from "react";
import Navbar from "./Components/Navbar";
import SearchBar from "./Components/SearchBar";
import Footer from "./Components/Footer";
import Cs from "./Components/Cs";
import PlaceDetails from "./Components/PlaceDetails"; // Import PlaceDetails page
import { BrowserRouter as Router, Routes, Route, useParams } from 'react-router-dom'; // Import routing components
import "./App.css"; // Ensure the CSS is properly linked

function App() {
  const [placeName, setPlaceName] = useState(""); // State to hold the place name
  const [nearbyHotels, setNearbyHotels] = useState([]);

  // Dummy hotel data
  const dummyHotelsData = [
    { id: 1, name: "Hotel Paradise", rating: 4.5 },
    { id: 2, name: "Seaside Resort", rating: 4.0 },
    { id: 3, name: "Mountain View Lodge", rating: 4.2 },
  ];

  // Hardcoded trending posts data
  const trendingPostsData = [
    {
      id: 1,
      title: "Exploring the Mountains",
      excerpt: "Join us on an adventure to the highest peaks.",
      image: "data:image/jpeg;base64,", // Sample image URL
    },
    {
      id: 2,
      title: "City Lights and Night Vibes",
      excerpt: "Discover the best cities to visit at night.",
      image: "data:image/jpeg;base64,", // Sample image URL
    },
    {
      id: 3,
      title: "serene meanders",
      excerpt: "Relax and unwind on the world's most beautiful meanders.",
      image: "data:image/jpeg;base64,", // Sample image URL
    },
  ];

  // Function to simulate fetching hotels based on place
  useEffect(() => {
    if (placeName) {
      setNearbyHotels(dummyHotelsData); // Set dummy hotel data when a place is shown
    }
  }, [placeName]);

  // Component to update the right panel with nearby hotels or trending posts
  const TrendingOrHotelsPanel = () => {
    return (
      <div className="col-md-4 right-panel d-flex flex-column justify-content-center align-items-center">
        {placeName ? (
          <>
            <h4 className="text-center">Best hotels nearby {placeName}</h4>
            <div className="hotels-list-wrapper">
              {nearbyHotels.length > 0 ? (
                nearbyHotels.map((hotel) => (
                  <div key={hotel.id} className="hotel-item text-center">
                    <h5>{hotel.name}</h5>
                    <p>Rating: {hotel.rating}⭐</p>
                  </div>
                ))
              ) : (
                <p>No hotels found</p>
              )}
            </div>
          </>
        ) : (
          <>
            <h4 className="text-center">Top Trending Places to Visit</h4>
            <div className="trending-posts-wrapper">
              {trendingPostsData.length > 0 ? (
                trendingPostsData.map((post) => (
                  <div key={post.id} className="trending-post text-center">
                    <img
                      src={post.image}
                      alt={post.title}
                      className="post-image mx-auto"
                    />
                    <div className="post-content">
                      <h5>{post.title}</h5>
                      <p>{post.excerpt}</p>
                      <a href={`/post/${post.id}`} className="btn btn-link">
                        Read More
                      </a>
                    </div>
                  </div>
                ))
              ) : (
                <p>No trending posts available</p>
              )}
            </div>
          </>
        )}
      </div>
    );
  };

  // When navigating to a place, update the place name
  const PlaceDetailsWrapper = () => {
    const { placeName } = useParams();
    useEffect(() => {
      setPlaceName(placeName); // Update placeName when a route param is available
    }, [placeName]);
    return <PlaceDetails />;
  };

  return (
    <Router>
      <div className="App">
        {/* Navbar */}
        <Navbar />
        <br />

        <div className="container-fluid content-wrapper">
          <div className="row content-border">
            {/* Main Content Area - Left 2/3 */}
            <div className="col-md-8 left-panel d-flex flex-column align-items-center">
              {/* Routes for Search and Place Details */}
              <Routes>
                <Route
                  path="/"
                  element={
                    <div className="left-content">
                      <div className="searchbar-wrapper">
                        <SearchBar /> {/* Fixed width for the search bar */}
                      </div>
                      <br />
                      <div className="carousel-wrapper">
                        <Cs /> {/* Carousel remains independent */}
                      </div>
                    </div>
                  }
                />
                <Route path="/place/:placeName" element={<PlaceDetailsWrapper />} />
              </Routes>
            </div>

            {/* Trending/Hotels Panel - Right 1/3 */}
            <TrendingOrHotelsPanel />
          </div>
        </div>

        {/* Footer */}
        <br />
        <Footer />
      </div>
    </Router>
  );
}

export default App;
